mapper.select:
select * from ( SELECT t.*,GROUP_CONCAT(d.target_range) AS target_range, GROUP_CONCAT(d.target_type) AS target_type FROM annunciate t LEFT JOIN annunciate_target d ON t.id=d.`annunciate_id` WHERE 1 = 1 and t.firm_id=#{firmId,jdbcType=BIGINT}
AND t.title like CONCAT('%',#{title,jdbcType=VARCHAR},'%')
AND t.send_state =#{sendState,jdbcType=VARCHAR}
and t.create_time >= #{createTimeStart,jdbcType=TIMESTAMP}
and t.create_time <= #{createTimeEnd,jdbcType=TIMESTAMP}
GROUP BY t.id order by ${sort} ${order})tt where 1=1
AND tt.target_type like CONCAT('%',#{targetType,jdbcType=INTEGER},'%')

mapper.update:
update annunciate

  version = version+1,
  read_count=read_count+#{readCount,jdbcType=INTEGER}

where id=#{id,jdbcType=BIGINT}

update.foreach:
update annunciate
set
  version = version+1,
  read_count=read_count+#{item.readCount}
where id=#{item.id}

mapper.update:
update annunciate

  stick_state=1

where firm_id=#{firmId,jdbcType=BIGINT}
and send_state=#{sendState,jdbcType=INTEGER}
and end_time>#{endTime,jdbcType=TIMESTAMP}
